<template>
  <div
    class="sixDragon"
    :class="{ disabled }">
    <div
      ref="logic"
      id="logic"></div>
    <div
      class="btns"
      style="position: fixed; right: 400px; top: 20px; z-index: 9999999999"
      v-if="!disabled">
      <div
        class="el-button"
        @click="saveData(logicType)">
        接口保存
      </div>
      <div
        class="el-button"
        @click="initData(toggle)">
        {{ toggle ? '打开' : '关闭' }}可拖拽宽高
      </div>
      <div
        class="el-button"
        @click="setLineType('customLineEdge')">
        设置线条类型为直线
      </div>
      <div
        class="el-button"
        @click="setLineType('customEdge')">
        设置线条类型为自动转弯线条
      </div>
      <div
        class="el-button"
        @click="triggerJSONUpload">
        导入json
      </div>
      <div
        class="el-button"
        @click="exportJSONFile">
        导出json
      </div>
    </div>

  </div>
</template>
<script>
import LogicFlow from '@logicflow/core';
import '@logicflow/core/dist/index.css';
import { Control, DndPanel, Menu } from '@logicflow/extension';
import '@logicflow/extension/lib/style/index.css';
import textRed from './js/textRed.js';
import textBlue from './js/textBlue.js';
import customEdge from './js/customEdge.js';
import smallCircle from './js/smallCircle.js';
import smallRect from './js/smallRect.js';
import smallEmptyRect from './js/smallEmptyRect.js';
import emptyNode from './js/emptyNode.js';
import custtomRectGreen from './js/custtomRectGreen.js';
import custtomRectBlue from './js/custtomRectBlue.js';
import custtomRectRed from './js/custtomRectRed.js';
import custtomRectYellow from './js/custtomRectYellow.js';
import customLineEdge from './js/customLineEdge.js';
import drawApi from "@/views/vxeModal/sixDrageon/api/drawer-api.js";
export default {
  props: {
    id: {
      type: String,
    },
    disabled: {
      type: Boolean,
      default: false,
    },
  },
  data() {
    return {
      lf: null,
      options: [],
      toggle: true,
      currentNode: {
        properties: {},
      },
    };
  },
  watch: {},
  mounted() {
    this.initData(this.toggle);
  },
  methods: {
    initData(bool = true) {
      this.lfUpdate(bool);
      this.getData();
    },
    /** 导入json */
    triggerJSONUpload() {
      // 1. 创建一个隐藏的 input 元素
      const input = document.createElement('input');
      input.type = 'file';
      input.accept = '.json';
      input.style.display = 'none';
      const that = this;
      // 2. 监听文件选择
      input.addEventListener('change', function () {
        const file = this.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = function (e) {
          try {
            const json = JSON.parse(e.target.result);
            that.lf.render(json);
          } catch (err) {}
        };
        reader.readAsText(file);
      });
      // 3. 手动触发点击
      document.body.appendChild(input); // 添加到 DOM 才能触发
      input.click();
      document.body.removeChild(input); // 上传完成后移除
    },
    /** 导入json */
    exportJSONFile() {
      const jsonData = this.lf.getGraphData();
      const content = JSON.stringify(jsonData, null, 2); // 格式化 JSON，带换行和缩进
      const blob = new Blob([content], { type: 'application/json' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'data.json';
      link.click();
      URL.revokeObjectURL(link.href);
    },
    lfUpdate(bool) {

      // lf初始化
      const lf = new LogicFlow({
        edgeType: 'customEdge',
        container: this.$refs.logic,
        stopMoveGraph: true,
        stopScrollGraph: true,
        stopZoomGraph: true,
        allowResize: bool,
        allowRotate: true,
        background: {
          backgroundColor: '#041323',
        },
        grid: {
          visible: false,
        },
        plugins: [
          DndPanel, // 拖拽面板
          Menu, // 节点面板
          Control, // 控制面板
        ],
        isSilentMode: this.disabled,
      });
      this.toggle = !this.toggle;
      this.lf = lf;
      // 拖拽面板
      const menuList = [
        {
          type: 'custtomRectGreen',
          label: '绿色框',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'custtomRectRed',
          label: '红色框',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'custtomRectBlue',
          label: '蓝色框',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'custtomRectYellow',
          label: '黄色框',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'emptyNode',
          label: '占位元素',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'smallCircle',
          label: '空心小圆',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'smallRect',
          label: '实心小方块',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'smallEmptyRect',
          label: '空心小方块',
          icon: 'data:image/png;base64,',
        },
        {
          type: 'textRed',
          label: '文字',
          icon: 'data:image/png;base64,',
        },
      ];
      // 增加右键菜单
      const that = this;
      lf.extension.menu.addMenuConfig({
        edgeMenu: [
          {
            text: '设置为完整线条',
            callback(edge) {
              lf.setProperties(edge.id, {
                fill: '#3ABCE7',
                strokeDasharray: '0 0',
              });
            },
          },
          {
            text: '设置为间隔线段（大）',
            callback(edge) {
              lf.setProperties(edge.id, {
                fill: '#3ABCE7',
                strokeDasharray: '14 7',
              });
            },
          },
          {
            text: '设置为间隔线段（小）',
            callback(edge) {
              lf.setProperties(edge.id, {
                fill: '#3ABCE7',
                strokeDasharray: '8 3',
              });
            },
          },
          {
            text: '设置为淡蓝色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#23cafb' });
            },
          },
          {
            text: '设置为深蓝色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#00a2ff' });
            },
          },
          {
            text: '设置为黄色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#e9c451' });
            },
          },
          {
            text: '设置为绿色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#10c89e' });
            },
          },
          {
            text: '设置为红色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#ef2a50' });
            },
          },

        ],
        nodeMenu: [
          {
            text: '设置为透明',
            callback(node) {
              lf.setProperties(node.id, { fill: 'transparent' });
            },
          },
          {
            text: '设置为淡蓝色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#23cafb' });
            },
          },
          {
            text: '设置为深蓝色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#00a2ff' });
            },
          },
          {
            text: '设置为黄色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#e9c451' });
            },
          },
          {
            text: '设置为绿色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#10c89e' });
            },
          },
          {
            text: '设置为红色',
            callback(node) {
              lf.setProperties(node.id, { fill: '#ef2a50' });
            },
          },

        ],
      });
      lf.extension.dndPanel.setPatternItems([...menuList]);
      lf.register(customEdge);
      lf.register(customLineEdge);

      lf.register(emptyNode);
      lf.register(textBlue);
      lf.register(textRed);
      lf.register(smallCircle);
      lf.register(smallRect);
      lf.register(smallEmptyRect);
      lf.register(custtomRectGreen);
      lf.register(custtomRectBlue);
      lf.register(custtomRectRed);
      lf.register(custtomRectYellow);
      lf.render([]);
    },
    setLineType(type) {
      this.lf.setDefaultEdgeType(type);
      this.$message.success('已切换线条类型');
    },
    // 保存节点和线条
    saveData() {
      // 保存节点
      drawApi.saveJsonDataService1({id:this.id,nodes:this.lf.getGraphData().nodes}).then(()=>{
        this.$message.success('保存节点成功');
      })
      // 保存线路
      drawApi.saveJsonDataService2({id:this.id,edges:this.lf.getGraphData().edges}).then(()=>{
        this.$message.success('保存线路成功');
      })
    },

    // 查询完整json
    async getData() {
      drawApi.loadJsonDataService({id:this.id}).then(res=>{
        this.lf.render(res.data);
      })
    },


  },
};
</script>
<style lang="scss" scoped>
.sixDragon {
  width: 100%;
  height: 100%;
}
.disabled {
  :deep(.lf-dndpanel) {
    display: none;
  }
}
:deep(#logic) {
  width: 100%;
  height: 100%;
  .lf-text-tspan {
    text-anchor: start;
  }
}
</style>
